import React from "react";

import { HandySvg } from "handy-svg";
import styles from "./PublicPost.module.scss";
import { MyApp } from "./Calendar/Calendar";
export function PublicPost() {
  const [activeInput, setActiveInput] = React.useState(true);
  const [value, onChange] = React.useState(new Date());
  const [openCal, setOpenCal] = React.useState(false);
  const listPublic = ["Видно всем", "Видно друзьям", "Выбать Близких друзей"];
  const [indexListPublic, setIndexListPublic] = React.useState(0);
  const boxPost = [
    {
      url: "/public/ico/box_post/item_photo.svg",
      class: `${styles.postPhoto} photo`,
      clue: "Фотография",
      sep: "",
    },
    {
      url: "/public/ico/box_post/item_video.svg",
      class: styles.postVideo,
      clue: "Видеозапись",
      sep: "",
    },
    {
      url: "/public/ico/box_post/item_audio.svg",
      class: styles.postAudio,
      clue: "Аудиозапись",
      sep: "",
    },
    {
      url: "/public/ico/box_post/item_article.svg",
      class: styles.postArticle,
      clue: "Статья",
      sep: "",
    },
    {
      url: "/public/ico/box_post/item_short_video.svg",
      class: styles.postShort,
      clue: "Клип",
      sep: `${styles.sep} sep`,
    },
    {
      url: "/public/ico/box_post/BestFriendsSettingsEntrypoint__layout.svg",
      clue: "Выбор близких друзей",
      class: styles.postSettings,
      sep: "",
    },
  ];
  return (
    <div
      onBlur={() => {
        console.log("Фокус потерян");
        // setActiveInput(false);
      }}
      onClick={() => {
        console.log("Что у вас нового?");
        setActiveInput(true);
      }}
      className={`${styles.box_post} boxBorder`}>
      <label className={styles.post} id="inputPost">
        <div className={styles.inputPost}>
          <img src="/public/ico/headerNav/foto.jpg" alt="" />
          <input
            onClick={() => {
              console.log("Что у вас нового?");
              setActiveInput(true);
            }}
            placeholder="Что у вас нового?"
            id="inputPost"
            type="text"
          />
        </div>

        <div className={styles.nav}>
          {activeInput ? (
            <div>
              <button className={`${styles.svgBtn}`}>
                <HandySvg src="/public/ico/box_post/em.svg" className={`${styles.svg}`} />
              </button>
            </div>
          ) : (
            <ul>
              {boxPost.map((item, index) => {
                return (
                  <li key={index}>
                    <button className={`${styles.svgBtn}`}>
                      <HandySvg src={item.url} className={`${styles.svg}`} />
                    </button>
                    {item.sep && <div key={index} className={item.sep}></div>}
                  </li>
                );
              })}
            </ul>
          )}
        </div>
      </label>
      {activeInput && (
        <div>
          <div className={`${styles.sepActive} sep`}></div>
          <div className={styles.postVisibility}>
            <div className={styles.publicationFor}>
              <ul>
                {listPublic.map((item, index) => {
                  return (
                    <li>
                      <button
                        onClick={() => {
                          setIndexListPublic(index);
                        }}>
                        <span>{item}</span>{" "}
                        {indexListPublic === index && (
                          <svg
                            width="16"
                            height="16"
                            viewBox="0 0 16 16"
                            fill="none"
                            xmlns="http://www.w3.org/2000/svg">
                            <path
                              d="M13.74 3.76c.35.34.35.9 0 1.23l-7.31 7.25a.88.88 0 0 1-1.25 0L2.26 9.36a.87.87 0 0 1 0-1.23.88.88 0 0 1 1.24 0l2.3 2.26 6.7-6.63a.88.88 0 0 1 1.24 0Z"
                              fill="currentColor"></path>
                          </svg>
                        )}
                      </button>
                    </li>
                  );
                })}
              </ul>
            </div>
            <div className={styles.calc}>
              <button
                onClick={() => {
                  setOpenCal(!openCal);
                }}>
                <span>Сейчас</span>
              </button>
              {openCal && <MyApp value={value} onChange={onChange} className={`boxBorder`} />}
              {<p>{value.toLocaleDateString()}</p>}
            </div>
          </div>
          <div className="sep" style={{ borderTop: "none" }}></div>
        </div>
      )}
    </div>
  );
}
