  const [statusLike, setStatusLike] = React.useState(data.like.status);
  const [statusRepost, setStatusRepost] = React.useState(data.repost.status);
  console.log(data);
  return (
    <div className={styles.post__likeBlock}>
      <div className={styles.post__likeBlock_likebtn}>
        <div className={`${styles.likebtn_like} `}>
          <span>
            <HandySvg
              className={`post_header_btn img_SVG_color ${stutusLike ? "active_SVG" : ""}`}
              src="/public/ico/post_btn/like_btn.svg"
            />
          </span>
        </div>
        {data.comment && (
          <div className={`${styles.likebtn_comment} `}>
            <span>
              <HandySvg
                className={`post_header_btn img_SVG_color`}
                src="/public/ico/post_btn/comments_btn.svg"
              />
            </span>
          </div>
        )}
        <div className={`${styles.likebtn_repost} `}>
          <span>
            <HandySvg
              className={`post_header_btn img_SVG_color `}
              src="/public/ico/post_btn/repost_btn.svg"
            />
          </span>
        </div>
      </div>
      <div className={styles.post__likeBlock_likeViews}> </div>
    </div>
  );
}
