import React from "react";
import { HandySvg } from "handy-svg";
import styles from "./PublicPost.module.scss";
//
let nameInput = "inputPost";
let clickDOM = "";
// function clicDOMElement(event) {
//   console.log(event.srcElement.localName);
//   clickDOM = event.srcElement.name;
// }

export function PublicPost() {
  // const [clickInput, setClickInput] = React.useState(clickDOM);
  const [s, setS] = React.useState(
    window.addEventListener("click", (event) => {
      console.log(event.srcElement.localName);
      return event.srcElement.name;
    }),
  );
  const [indexClue, setIndexClue] = React.useState("");
  function onMouseTrue(i) {}
  function onMouseFalse() {}
  const boxPost = [
    {
      url: "/public/ico/box_post/item_photo.svg",
      class: `${styles.postPhoto} photo`,
      clue: "Фотография",
      sep: "",
    },
    {
      url: "/public/ico/box_post/item_video.svg",
      class: styles.postVideo,
      clue: "Видеозапись",
      sep: "",
    },
    {
      url: "/public/ico/box_post/item_audio.svg",
      class: styles.postAudio,
      clue: "Аудиозапись",
      sep: "",
    },
    {
      url: "/public/ico/box_post/item_article.svg",
      class: styles.postArticle,
      clue: "Статья",
      sep: "",
    },
    {
      url: "/public/ico/box_post/item_short_video.svg",
      class: styles.postShort,
      clue: "Клип",
      sep: `${styles.sep} sep`,
    },
    {
      url: "/public/ico/box_post/BestFriendsSettingsEntrypoint__layout.svg",
      clue: "Выбор близких друзей",
      class: styles.postSettings,
      sep: "",
    },
  ];
  return (
    <div className={`${styles.box_post} boxBorder`}>
      <label className={styles.post} name="inputPost">
        <div className={styles.inputPost}>
          <img src="/public/ico/headerNav/foto.jpg" alt="" />
          <input
            onClick={(event) => {
              console.log(s);
              if (event.target.name === clickDOM) {
                console.log(true, event.target.name);
              } else {
                console.log(false, clickDOM);
              }
            }}
            placeholder="Что у вас нового?"
            name="inputPost"
            type="text"
          />
        </div>

        <div className={styles.nav}>
          <ul>
            {boxPost.map((item, index) => {
              return (
                <li key={index}>
                  <button
                    stop={index}
                    onMouseEnter={(event) => {
                      onMouseTrue(index);
                    }}
                    onMouseLeave={onMouseFalse}>
                    <HandySvg src={item.url} className={`${styles.svg}`} />
                  </button>
                  {item.sep && <div key={index} className={item.sep}></div>}
                </li>
              );
            })}
          </ul>
        </div>
      </label>
    </div>
  );
}

