import React from "react";

class Tooltip extends React.Component {
  constructor(props) {
    super(props);
    this.toggle = this.toggle.bind(this);
    this.state = { opacity: false };
  }

  toggle() {
    const tooltipNode = ReactDOM.findDOMNode(this); // находит Tooltip элемент
    this.setState({ opacity: !this.state.opacity, top: tooltipNode.offsetTop });
  }

  // render() {}
}

export function TextContent() {
  return (
    <div>
      <h1>
        Никогда не наводи на<Tooltip text="КУКУ"> меня</Tooltip>
      </h1>
    </div>
  );
}
