import React from "react";

import styles from "./news.module.scss";
// left
import { PublicPost } from "../../components/publicPost";
import { Post } from "../../components/Post/Post";
// right
import { UiMenu } from "../../components/uiMenu";
import { UiToggler } from "../../components/uiToggler";
export function News({ dataPost }) {
  const [activeInput, setActiveInput] = React.useState(false);
  const [scrollI, setScrollI] = React.useState(0);

  const setParallax = (selector) => {
    const scrollElement = document.querySelector(selector);
    // console.log(scrollElement);
    const scrolled = scrollElement.getBoundingClientRect();
    console.log(scrolled);

    scrollElement.style.marginTop = `0 ${0 - scrolled * 0.25 + "px"}`;
  };

  window.addEventListener("scroll", () => {
    setParallax(`.${styles.narrow_column}`);
  });

  return (
    <div className={styles.news}>
      <div className={styles.wide_column}>
        <PublicPost activeInput={activeInput} setActiveInput={setActiveInput} />
        {dataPost.map((item, index) => {
          return <Post {...item} activeInput={activeInput} />;
        })}
      </div>{" "}
      <div className={styles.narrow_column}>
        <UiMenu />
        <UiToggler />
      </div>
    </div>
  );
}
